import { useDelete } from "../../hooks/useDelete";
import  { useState } from "react";
import EditarVotacao from "../pagina_principal/EditarVotacao";
const BtnVoto = ({setAbriModalVoto, setMostrarImg, pauta, idAssociado}) =>{
     const {httpConfigDeletar} = useDelete();
    const [editar,setEditar] = useState(false);
    const editarVotacao = () =>{
        setAbriModalVoto(false)
       setEditar(true)
        
    }
    const voltar = () => {
        if (setAbriModalVoto) {
            setAbriModalVoto(false);
            setMostrarImg(true);
        }
    }
    const deletarVotacao = () => {
        httpConfigDeletar(pauta.id, "DELETE")
    }

    
    return(
          
            <div className="botoes-acoes espacamento-superior">
                {editar && <EditarVotacao idPauta = {pauta.id} setEditar = {setEditar} setMostrarImg = {setMostrarImg}/>}
                        <button onClick={voltar}>voltar</button>
                      
                        {pauta.idAutor === idAssociado && (
                            <>
                                <button className="btn-editar" onClick={editarVotacao}>Editar</button>
                                 <button className="btn-deletar" onClick={() => {
                                    if (window.confirm("Tem certeza que deseja deletar esta votação?")) {
                                        deletarVotacao();
                                    }
                                }}>Deletar</button>
                            </>
                        )}
                    </div>
    )
     
}
export default BtnVoto;
