import { useEffect, useState } from "react";

export const usePut = () => {
    const [config, setConfig] = useState(null);
    const [url, setUrl] = useState(null);
    const [carregamento, setCarregamento] = useState(false);
    const [erro, setErro] = useState(null);

   const httpPutConfig = (dados, idPauta, url) =>{
        setErro("");
        let putUrl= url + `${idPauta}`
        setConfig({
             method: "PUT",
              headers: {
                "Content-type": "application/json"
            },
              body: JSON.stringify(dados)
        })

       
    }
    console.log(url)
    const limparErro = () => setErro("");
    useEffect(()=>{
        const httpRequest = async () =>{
              if (!config) return;
            setCarregamento(true);
             setUrl(putUrl);
            try {
                const requisicao = await fetch(url, config);
                if(!requisicao.ok){
                    let erroRequisicao = await requisicao.json();
                    throw {mensagem: erroRequisicao.mensagem, status: erroRequisicao.status}
                }

            } catch (error) {
                setErro({
                    mensagem:error.mensagem,
                    status:error.status
                });
                 return null
            }
            finally{
                setCarregamento(true);
            }
           
        };
        httpRequest();
    },[config,url])
    return {httpPutConfig, carregamento, erro, limparErro}

}
