import { useGet } from "../../hooks/useGet";
import { usePost } from "../../hooks/usePost";
import React, { useState, useEffect } from "react";
const Voto = ({ idAssociado, idPauta, setAbriModalVoto, setMostrarImg }) => {
    const [idVotacao, setIdVotacao] = useState("");
    const urlVotacao = `http://localhost:8080/v1/votacoes/pauta/${idPauta}`;
    const urlVotar = `http://localhost:8080/v1/votos/${idAssociado}/${idVotacao}`;
    const urlPauta = `http://localhost:8080/v1/pautas/${idPauta}` 
    const { httpConfig, carregamento, erro, limparErro } = usePost();
    const { dados } = useGet(urlVotacao);
    useEffect(() => {
        limparErro();
    }, []);
    useEffect(() => {

        if (dados) {
         
            setIdVotacao(dados.id);
        }
    }, [dados]);

    const computarVotoSim = () => {
        const opcao = {
            voto: "sim"
        }
        httpConfig(opcao, "POST", urlVotar);
    }
    const computarVotoNao = () => {
        const opcao = {
            voto: "nao"
        }
        httpConfig(opcao, "POST", urlVotar);
    }
    const voltar = () =>{
        if(setAbriModalVoto){
            setAbriModalVoto(false);
            setMostrarImg(true);
        }
    }

    return (
        <article>
            <h3>Opção de votação</h3>
            {carregamento ? (
                <p>Carregando...</p>
            ) : (
               <div className="caixa-votacao-aberta">
                    <button onClick={computarVotoSim}>SIM</button>
                    <button onClick={computarVotoNao}>NÃO</button>
                    <span> <button onClick={voltar}>voltar</button></span>
                   
                </div>
            )}
            {erro && (
                <>
                    <p>{erro.status}</p>
                    <p>{erro.mensagem}</p>
                </>
            )}
        </article>

    )

}

export default Voto;
