import React, { useState } from "react";
import "./MenuPrincipal.css";
import icon from "./assets/icon.png";
import lateralImage from "./assets/menu.png";

export const MenuPrincipal = ({ numeroUsuario, cpfUsuario }) => {
    const nomeUsuario = "Wanderllan Santos";

    // Estado inicial das votações atualizado para conter usuários que votaram e votos separados por opção
    const [votacoes, setVotacoes] = useState([
        {
            titulo: "Qual seu estilo musical favorito?",
            autor: "João Silva",
            votosOpcao1: 70,
            votosOpcao2: 50,
            usuariosVotaram: [123, 456],
            opcao1: "Rock",
            opcao2: "Pop",
        },
        {
            titulo: "Melhor série do momento",
            autor: "Maria Souza",
            votosOpcao1: 40,
            votosOpcao2: 45,
            usuariosVotaram: [789],
            opcao1: "Breaking Bad",
            opcao2: "Stranger Things",
        },
        {
            titulo: "Você prefere café ou chá?",
            autor: "Wanderllan Santos",
            votosOpcao1: 35,
            votosOpcao2: 25,
            usuariosVotaram: [],
            opcao1: "Café",
            opcao2: "Chá",
        },
    ]);

    const [votacaoAberta, setVotacaoAberta] = useState(null);
    const [criandoVotacao, setCriandoVotacao] = useState(false);
    const [editandoVotacaoIndex, setEditandoVotacaoIndex] = useState(null);

    const [novaVotacao, setNovaVotacao] = useState({
        titulo: "",
        opcao1: "",
        opcao2: "",
    });

    // Mensagem para o usuário após votar ou ao tentar votar novamente
    const [mensagemVoto, setMensagemVoto] = useState(null);
    // Tipo da mensagem ('sucesso' ou 'erro') para estilos
    const [tipoMensagem, setTipoMensagem] = useState(null);

    // Abrir uma votação para votar
    const abrirVotacao = (index) => {
        setVotacaoAberta(index);
        setMensagemVoto(null);
        setTipoMensagem(null);
    };

    const fecharVotacao = () => {
        setVotacaoAberta(null);
        setMensagemVoto(null);
        setTipoMensagem(null);
    };

    // Iniciar criação da votação
    const iniciarCriacao = () => {
        setNovaVotacao({ titulo: "", opcao1: "", opcao2: "" });
        setCriandoVotacao(true);
        setEditandoVotacaoIndex(null);
    };

    // Salvar nova votação ou edição
    const salvarNovaVotacao = () => {
        if (!novaVotacao.titulo.trim() || !novaVotacao.opcao1.trim() || !novaVotacao.opcao2.trim()) {
            alert("Preencha todos os campos!");
            return;
        }
        if (editandoVotacaoIndex !== null) {
            const atualizadas = [...votacoes];
            atualizadas[editandoVotacaoIndex].titulo = novaVotacao.titulo;
            atualizadas[editandoVotacaoIndex].opcao1 = novaVotacao.opcao1;
            atualizadas[editandoVotacaoIndex].opcao2 = novaVotacao.opcao2;
            setVotacoes(atualizadas);
        } else {
            setVotacoes([
                ...votacoes,
                {
                    titulo: novaVotacao.titulo,
                    autor: nomeUsuario,
                    votosOpcao1: 0,
                    votosOpcao2: 0,
                    usuariosVotaram: [],
                    opcao1: novaVotacao.opcao1,
                    opcao2: novaVotacao.opcao2,
                },
            ]);
        }
        setCriandoVotacao(false);
    };

    const editarVotacao = (index) => {
        setNovaVotacao({
            titulo: votacoes[index].titulo,
            opcao1: votacoes[index].opcao1,
            opcao2: votacoes[index].opcao2,
        });
        setEditandoVotacaoIndex(index);
        setCriandoVotacao(true);
    };

    const cancelarCriacao = () => {
        setCriandoVotacao(false);
    };

    // Função para votar numa opção
    const votar = (opcao) => {
        if (votacaoAberta === null) return;

        const votacaoAtual = votacoes[votacaoAberta];
        const jaVotou = votacaoAtual.usuariosVotaram.includes(numeroUsuario);

        if (jaVotou) {
            setMensagemVoto("⚠️ Você já votou nesta votação.");
            setTipoMensagem("erro");
            return;
        }

        // Incrementa o voto na opção selecionada
        const atualizadas = [...votacoes];
        if (opcao === 1) {
            atualizadas[votacaoAberta].votosOpcao1 += 1;
        } else if (opcao === 2) {
            atualizadas[votacaoAberta].votosOpcao2 += 1;
        }
        // Marca usuário como votante
        atualizadas[votacaoAberta].usuariosVotaram.push(numeroUsuario);
        setVotacoes(atualizadas);

        setMensagemVoto("Obrigado pela sua opinião!");
        setTipoMensagem("sucesso");

        // Fecha automaticamente após 2 segundos
        setTimeout(() => {
            fecharVotacao();
            setMensagemVoto(null);
            setTipoMensagem(null);
        }, 2000);
    };

    // Calcula % dos votos para as barras
    const calcPercent = (votos1, votos2) => {
        const total = votos1 + votos2;
        if (total === 0) return [50, 50];
        const p1 = Math.round((votos1 / total) * 100);
        const p2 = 100 - p1;
        return [p1, p2];
    };

    return (
        <div className="menu">
            <div className="barra-superior">
                <div className="coluna info-usuario">
          <span className="nome-usuario">
            {nomeUsuario} {numeroUsuario}
          </span>
                    <span className="cpf-usuario">{cpfUsuario}</span>
                </div>
                <div className="coluna imagem-logo">
                    <img src={icon} alt="Logo" />
                </div>
                <div className="coluna botao-wrapper">
                    <button className="botao-deslogar" onClick={() => console.log("Deslogado")}>
                        Sair
                    </button>
                </div>
            </div>

            <div className="imagem-lateral">
                <img src={lateralImage} alt="Descrição visual" />
            </div>

            {criandoVotacao ? (
                <div className="caixa-votacao-aberta">
                    <h3>{editandoVotacaoIndex !== null ? "Editar Votação" : "Nova Votação"}</h3>
                    <input
                        type="text"
                        placeholder="Tema da Votação"
                        value={novaVotacao.titulo}
                        onChange={(e) => setNovaVotacao({ ...novaVotacao, titulo: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Opção 1"
                        value={novaVotacao.opcao1}
                        onChange={(e) => setNovaVotacao({ ...novaVotacao, opcao1: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Opção 2"
                        value={novaVotacao.opcao2}
                        onChange={(e) => setNovaVotacao({ ...novaVotacao, opcao2: e.target.value })}
                    />
                    <button className="botao-votar" onClick={salvarNovaVotacao}>
                        Salvar
                    </button>
                    <button className="botao-fechar" onClick={cancelarCriacao}>
                        Cancelar
                    </button>
                </div>
            ) : votacaoAberta !== null ? (
                <div className="caixa-votacao-aberta">
                    <h3>{votacoes[votacaoAberta].titulo}</h3>
                    <p>
                        Criada por <strong>{votacoes[votacaoAberta].autor}</strong>
                    </p>

                    {/* Barra única com as duas cores lado a lado */}
                    {(() => {
                        const { votosOpcao1, votosOpcao2 } = votacoes[votacaoAberta];
                        const [p1, p2] = calcPercent(votosOpcao1, votosOpcao2);
                        return (
                            <div
                                style={{
                                    display: "flex",
                                    width: "100%",
                                    height: "30px",
                                    borderRadius: "10px",
                                    overflow: "hidden",
                                    marginBottom: "10px",
                                    border: "1px solid #ccc",
                                }}
                            >
                                <div
                                    style={{
                                        width: `${p1}%`,
                                        backgroundColor: "#4caf50",
                                        display: "flex",
                                        justifyContent: "center",
                                        alignItems: "center",
                                        color: "white",
                                        fontWeight: "bold",
                                        fontSize: "14px",
                                    }}
                                    title={`${votacoes[votacaoAberta].opcao1}: ${p1}% (${votosOpcao1} votos)`}
                                >
                                    {p1}%
                                </div>
                                <div
                                    style={{
                                        width: `${p2}%`,
                                        backgroundColor: "#2196f3",
                                        display: "flex",
                                        justifyContent: "center",
                                        alignItems: "center",
                                        color: "white",
                                        fontWeight: "bold",
                                        fontSize: "14px",
                                    }}
                                    title={`${votacoes[votacaoAberta].opcao2}: ${p2}% (${votosOpcao2} votos)`}
                                >
                                    {p2}%
                                </div>
                            </div>
                        );
                    })()}

                    {/* Botões para votar em cada opção */}
                    <div style={{ display: "flex", gap: "10px", marginBottom: "10px" }}>
                        <button className="botao-votar" onClick={() => votar(1)}>
                            {votacoes[votacaoAberta].opcao1}
                        </button>
                        <button className="botao-votar" onClick={() => votar(2)}>
                            {votacoes[votacaoAberta].opcao2}
                        </button>
                    </div>

                    {/* Mensagem após votar ou tentar votar */}
                    {mensagemVoto && (
                        <div
                            style={{
                                color: tipoMensagem === "erro" ? "red" : "green",
                                marginBottom: "10px",
                                fontWeight: "bold",
                            }}
                        >
                            {mensagemVoto}
                        </div>
                    )}

                    <p>
                        Total de votos: {votacoes[votacaoAberta].votosOpcao1 + votacoes[votacaoAberta].votosOpcao2}
                    </p>

                    <p>
                        Usuários que votaram:{" "}
                        {votacoes[votacaoAberta].usuariosVotaram.length > 0
                            ? votacoes[votacaoAberta].usuariosVotaram.join(", ")
                            : "Nenhum voto ainda"}
                    </p>

                    <button className="botao-fechar" onClick={fecharVotacao}>
                        Voltar
                    </button>
                </div>
            ) : (
                <div className="conteudo-principal">
                    <div className="texto-votacoes">
                        <h3>Votações em Aberto</h3>
                        <p>Selecione o seu interesse e faça sua votação</p>
                    </div>

                    <div className="caixa-votacoes">
                        <ul className="lista-votacoes">
                            {votacoes.map((votacao, index) => (
                                <li key={index} onClick={() => abrirVotacao(index)}>
                                    <strong>{votacao.titulo}</strong>
                                    <div className="subinfo">
                                        Por: {votacao.autor} •{" "}
                                        {votacao.votosOpcao1 + votacao.votosOpcao2} votos
                                        {votacao.autor === nomeUsuario && (
                                            <span
                                                style={{ float: "right", color: "#007BFF", cursor: "pointer" }}
                                                onClick={(e) => {
                                                    e.stopPropagation();
                                                    editarVotacao(index);
                                                }}
                                            >
                        Editar
                      </span>
                                        )}
                                    </div>
                                </li>
                            ))}
                            <li
                                onClick={iniciarCriacao}
                                style={{ backgroundColor: "#fff0f0", fontWeight: "bold" }}
                            >
                                + Nova Votação
                            </li>
                        </ul>
                    </div>
                </div>
            )}

            <footer className="rodape">
                <div className="header">
                    <h1>Turma da Unifacol</h1>
                    <h2>Sistema de Informação 5º Período</h2>
                </div>
            </footer>
        </div>
    );
};
