import { useGet } from "../../hooks/useGet";
import { usePost } from "../../hooks/usePost";
import React, { useState, useEffect } from "react";
const Voto = ({ idAssociado, idPauta, setAbriModalVoto, setAbrirMoldalVotacao, setMostrarImg }) => {
    const [votacao, setVotacao] = useState("");
    const urlVotacao = `http://localhost:8080/v1/votacoes/pauta/${idPauta}`;
    const urlVotar = `http://localhost:8080/v1/votos/${idAssociado}/${votacao.id}`;
    const { httpConfig, carregamento, erro, limparErro } = usePost();
    const { dados } = useGet(urlVotacao);

    const [pauta, setPauta] = useState("");
    useEffect(() => {
        limparErro();

    }, []);
    useEffect(() => {

        if (dados) {

            setVotacao(dados.id);
            setPauta(dados.pauta);
        }
    }, [dados]);

    const computarVotoSim = () => {
        const opcao = {
            voto: "sim"
        }
        httpConfig(opcao, "POST", urlVotar);
    }
    const computarVotoNao = () => {
        const opcao = {
            voto: "nao"
        }
        httpConfig(opcao, "POST", urlVotar);
    }
    const voltar = () => {
        if (setAbriModalVoto) {
            setAbriModalVoto(false);
            setMostrarImg(true);
        }
    }

    return (
        <article className="caixa-votacao-aberta">
            <h3>Opção de votação</h3>
            {carregamento ? (
                <p>Carregando...</p>
            ) : (
                <div >
                    <h3>{pauta.titulo}</h3>
                    <p>Criada por <strong>{pauta.autor}</strong></p>
                    <div className="botoes-opcoes">
                        <button onClick={computarVotoSim}>SIM</button>
                        <button onClick={computarVotoNao}>NÃO</button>
                    </div>
                    <p>Total de pessoas que votaram: {votacao.votos.length}</p>
                    <div className="botoes-acoes espacamento-superior">
                        <button onClick={voltar}>voltar</button>
                        {votacao.pauta.idAutor === idAssociado && (
                            <>
                                <button className="btn-editar" onClick={() => editarVotacao(votacaoAberta)}>Editar</button>

                            </>
                        )}
                    </div>

                </div>

            )}
            {erro && (
                <>
                    <p>{erro.status}</p>
                    <p>{erro.mensagem}</p>
                </>
            )}
        </article>

    )

}

export default Voto;
