import React from "react";
import "./MenuPrincipal.css";
import login from "./assets/login.png";
import lateralImage from "./assets/lateral.png"; // Substitua pelo nome correto da imagem lateral

export const MenuPrincipal = ({ numeroUsuario, cpfUsuario, votacoes = [] }) => {
    return (
        <div className="menu">
            {/* Barra superior com usuário e CPF */}
            <div className="barra-superior">
                <span>Nº Usuário: {numeroUsuario}</span>
                <span>CPF: {cpfUsuario}</span>
            </div>

            <div className="header">
                <h1>Turma da Unifacol</h1>
                <h2>Sistema de Informação 5º Período</h2>
            </div>

            <img className="line" alt="Linha decorativa" src={login} />

            <div className="perfil">
                <h3>Nome Completo</h3>
                <p>Descrição</p>
            </div>

            {/* Imagem lateral em vez da descrição em texto */}
            <div className="imagem-lateral">
                <img src={menu.png} alt="Descrição visual" />
            </div>

            {/* Votações em Aberto */}
            <div className="votacoes">
                <h3>Votações em Aberto</h3>
                <p>Selecione o seu interesse e faça sua votação</p>
                <ul className="lista-votacoes">
                    {votacoes.map((votacao, index) => (
                        <li key={index}>{votacao}</li>
                    ))}
                </ul>
            </div>
        </div>
    );
};
