import React, { useState } from "react";
import "./MenuPrincipal.css";
import icon from "./assets/icon.png";
import lateralImage from "./assets/menu.png";

export const MenuPrincipal = ({ numeroUsuario, cpfUsuario }) => {
    const nomeUsuario = "Wanderllan Santos";

    const [votacoes, setVotacoes] = useState([
        {
            titulo: "Qual seu estilo musical favorito?",
            autor: "João Silva",
            votosOpcao1: 70,
            votosOpcao2: 50,
            opcao1: "Rock",
            opcao2: "Pop",
            usuariosVotaram: ["user1", "user2", "user3"],
        },
        {
            titulo: "Melhor série do momento",
            autor: "Maria Souza",
            votosOpcao1: 30,
            votosOpcao2: 55,
            opcao1: "Breaking Bad",
            opcao2: "Game of Thrones",
            usuariosVotaram: ["user4", "user5"],
        },
        {
            titulo: "Você prefere café ou chá?",
            autor: "Wanderllan Santos",
            votosOpcao1: 35,
            votosOpcao2: 25,
            opcao1: "Café",
            opcao2: "Chá",
            usuariosVotaram: ["user6", "user7", "user8", "user9"],
        },
    ]);

    const [votacaoAberta, setVotacaoAberta] = useState(null);
    const [criandoVotacao, setCriandoVotacao] = useState(false);
    const [editandoVotacaoIndex, setEditandoVotacaoIndex] = useState(null);

    const [novaVotacao, setNovaVotacao] = useState({
        titulo: "",
        opcao1: "",
        opcao2: "",
    });

    const [mensagemVoto, setMensagemVoto] = useState("");
    const [tipoMensagem, setTipoMensagem] = useState("");

    const calcPercent = (v1, v2) => {
        const total = v1 + v2;
        if (total === 0) return [50, 50];
        const p1 = Math.round((v1 / total) * 100);
        const p2 = 100 - p1;
        return [p1, p2];
    };

    const abrirVotacao = (index) => {
        setMensagemVoto("");
        setTipoMensagem("");
        setVotacaoAberta(index);
    };

    const fecharVotacao = () => {
        setMensagemVoto("");
        setTipoMensagem("");
        setVotacaoAberta(null);
    };

    const iniciarCriacao = () => {
        setNovaVotacao({ titulo: "", opcao1: "", opcao2: "" });
        setCriandoVotacao(true);
        setEditandoVotacaoIndex(null);
        setMensagemVoto("");
        setTipoMensagem("");
    };

    const salvarNovaVotacao = () => {
        if (!novaVotacao.titulo || !novaVotacao.opcao1 || !novaVotacao.opcao2) {
            setMensagemVoto("Preencha todos os campos.");
            setTipoMensagem("erro");
            return;
        }
        if (editandoVotacaoIndex !== null) {
            const atualizadas = [...votacoes];
            atualizadas[editandoVotacaoIndex] = {
                ...atualizadas[editandoVotacaoIndex],
                titulo: novaVotacao.titulo,
                opcao1: novaVotacao.opcao1,
                opcao2: novaVotacao.opcao2,
            };
            setVotacoes(atualizadas);
        } else {
            setVotacoes([
                ...votacoes,
                {
                    titulo: novaVotacao.titulo,
                    autor: nomeUsuario,
                    votosOpcao1: 0,
                    votosOpcao2: 0,
                    opcao1: novaVotacao.opcao1,
                    opcao2: novaVotacao.opcao2,
                    usuariosVotaram: [],
                },
            ]);
        }
        setCriandoVotacao(false);
        setMensagemVoto("");
        setTipoMensagem("");
    };

    const editarVotacao = (index) => {
        const vot = votacoes[index];
        setNovaVotacao({
            titulo: vot.titulo,
            opcao1: vot.opcao1,
            opcao2: vot.opcao2,
        });
        setEditandoVotacaoIndex(index);
        setCriandoVotacao(true);
        setMensagemVoto("");
        setTipoMensagem("");
    };

    const cancelarCriacao = () => {
        setCriandoVotacao(false);
        setMensagemVoto("");
        setTipoMensagem("");
    };

    const votar = (opcao) => {
        const votacao = votacoes[votacaoAberta];
        const usuarioId = numeroUsuario;

        if (votacao.usuariosVotaram.includes(usuarioId)) {
            setMensagemVoto("⚠️ Você já votou nesta votação.");
            setTipoMensagem("erro");
            return;
        }

        const atualizadas = [...votacoes];
        if (opcao === 1) atualizadas[votacaoAberta].votosOpcao1++;
        else if (opcao === 2) atualizadas[votacaoAberta].votosOpcao2++;

        atualizadas[votacaoAberta].usuariosVotaram.push(usuarioId);

        setVotacoes(atualizadas);
        setMensagemVoto("Obrigado pela sua opinião!");
        setTipoMensagem("sucesso");

        setTimeout(() => {
            fecharVotacao();
        }, 2000);
    };

    const deletarVotacao = (index) => {
        const atualizadas = [...votacoes];
        atualizadas.splice(index, 1);
        setVotacoes(atualizadas);
        setVotacaoAberta(null);
        setMensagemVoto("");
        setTipoMensagem("");
    };

    return (
        <div className="menu">
            <div className="barra-superior">
                <div className="coluna info-usuario">
                    <span className="nome-usuario">
                        {nomeUsuario} {numeroUsuario}
                    </span>
                    <span className="cpf-usuario">{cpfUsuario}</span>
                </div>
                <div className="coluna imagem-logo">
                    <img src={icon} alt="Logo" />
                </div>
                <div className="coluna botao-wrapper">
                    <button className="botao-deslogar" onClick={() => console.log("Deslogado")}>
                        Sair
                    </button>
                </div>
            </div>

            <div className="imagem-lateral">
                <img src={lateralImage} alt="Descrição visual" />
            </div>

            {criandoVotacao ? (
                <div className="caixa-votacao-aberta">
                    <h3>{editandoVotacaoIndex !== null ? "Editar Votação" : "Nova Votação"}</h3>
                    <input
                        type="text"
                        placeholder="Tema da Votação"
                        value={novaVotacao.titulo}
                        onChange={(e) => setNovaVotacao({ ...novaVotacao, titulo: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Opção 1"
                        value={novaVotacao.opcao1}
                        onChange={(e) => setNovaVotacao({ ...novaVotacao, opcao1: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Opção 2"
                        value={novaVotacao.opcao2}
                        onChange={(e) => setNovaVotacao({ ...novaVotacao, opcao2: e.target.value })}
                    />
                    {mensagemVoto && (
                        <div
                            style={{
                                color: tipoMensagem === "erro" ? "red" : "green",
                                marginBottom: "10px",
                                fontWeight: "bold",
                            }}
                        >
                            {mensagemVoto}
                        </div>
                    )}
                    <button className="botao-votar" onClick={salvarNovaVotacao}>
                        Salvar
                    </button>
                    <button className="botao-fechar" onClick={cancelarCriacao}>
                        Cancelar
                    </button>
                </div>
            ) : votacaoAberta !== null ? (
                <div className="caixa-votacao-aberta">
                    <h3>{votacoes[votacaoAberta].titulo}</h3>
                    <p>
                        Criada por <strong>{votacoes[votacaoAberta].autor}</strong>
                    </p>

                    <div
                        style={{
                            display: "flex",
                            justifyContent: "space-between",
                            marginBottom: "4px",
                            fontWeight: "bold",
                        }}
                    >
                        <span>{votacoes[votacaoAberta].opcao1}</span>
                        <span>{votacoes[votacaoAberta].opcao2}</span>
                    </div>

                    {(() => {
                        const { votosOpcao1, votosOpcao2 } = votacoes[votacaoAberta];
                        const [p1, p2] = calcPercent(votosOpcao1, votosOpcao2);
                        return (
                            <div
                                style={{
                                    display: "flex",
                                    width: "100%",
                                    height: "30px",
                                    borderRadius: "10px",
                                    overflow: "hidden",
                                    marginBottom: "10px",
                                    border: "1px solid #ccc",
                                }}
                            >
                                <div
                                    style={{
                                        width: `${p1}%`,
                                        backgroundColor: "#4caf50",
                                        display: "flex",
                                        justifyContent: "center",
                                        alignItems: "center",
                                        color: "white",
                                        fontWeight: "bold",
                                        fontSize: "14px",
                                    }}
                                    title={`${votacoes[votacaoAberta].opcao1}: ${p1}% (${votosOpcao1} votos)`}
                                >
                                    {p1}%
                                </div>
                                <div
                                    style={{
                                        width: `${p2}%`,
                                        backgroundColor: "#2196f3",
                                        display: "flex",
                                        justifyContent: "center",
                                        alignItems: "center",
                                        color: "white",
                                        fontWeight: "bold",
                                        fontSize: "14px",
                                    }}
                                    title={`${votacoes[votacaoAberta].opcao2}: ${p2}% (${votosOpcao2} votos)`}
                                >
                                    {p2}%
                                </div>
                            </div>
                        );
                    })()}

                    <div style={{ display: "flex", gap: "10px", marginBottom: "10px" }}>
                        <button className="botao-votar" onClick={() => votar(1)}>
                            {votacoes[votacaoAberta].opcao1}
                        </button>
                        <button className="botao-votar" onClick={() => votar(2)}>
                            {votacoes[votacaoAberta].opcao2}
                        </button>
                    </div>

                    {mensagemVoto && (
                        <div
                            style={{
                                color: tipoMensagem === "erro" ? "red" : "green",
                                marginBottom: "10px",
                                fontWeight: "bold",
                            }}
                        >
                            {mensagemVoto}
                        </div>
                    )}

                    <p>Total de pessoas que votaram: {votacoes[votacaoAberta].usuariosVotaram.length}</p>

                    <div style={{ display: "flex", gap: "10px", marginTop: "10px" }}>
                        <button className="botao-fechar" onClick={fecharVotacao}>
                            Voltar
                        </button>

                        {votacoes[votacaoAberta].autor === nomeUsuario && (
                            <>
                                <button
                                    className="botao-editar"
                                    onClick={() => editarVotacao(votacaoAberta)}
                                >
                                    Editar
                                </button>

                                <button
                                    className="botao-deletar"
                                    onClick={() => {
                                        if (
                                            window.confirm(
                                                "Tem certeza que deseja deletar esta votação?"
                                            )
                                        ) {
                                            deletarVotacao(votacaoAberta);
                                        }
                                    }}
                                >
                                    Deletar
                                </button>
                            </>
                        )}
                    </div>
                </div>
            ) : (
                <div className="conteudo-principal">
                    <div className="texto-votacoes">
                        <h3>Votações em Aberto</h3>
                        <p>Selecione o seu interesse e faça sua votação</p>
                    </div>

                    <div className="caixa-votacoes">
                        <ul className="lista-votacoes">
                            {votacoes.map((votacao, index) => (
                                <li key={index} onClick={() => abrirVotacao(index)} className="item-votacao">
                                    <strong>{votacao.titulo}</strong>
                                    <div className="subinfo">
                                        Por: {votacao.autor} • {votacao.usuariosVotaram.length} votos
                                    </div>
                                </li>
                            ))}
                            <li onClick={iniciarCriacao} style={{ backgroundColor: "#fff0f0", fontWeight: "bold" }}>
                                + Nova Votação
                            </li>
                        </ul>
                    </div>
                </div>
            )}

            <footer className="rodape">
                <div className="header">
                    <h1>Turma da Unifacol</h1>
                    <p>Desenvolvido por Wanderllan Santos</p>
                </div>
                <div className="conteudo">
                    <p>
                        Para dúvidas e sugestões:{" "}
                        <a href="mailto:wanderllan@example.com">wanderllan@example.com</a>
                    </p>
                </div>
            </footer>
        </div>
    );
};
