import { useEffect, useState } from "react";

export const usePut = () => {
    const [config, setConfig] = useState(null);

    const [carregamento, setCarregamento] = useState(false);
    const [erro, setErro] = useState(null);

    const httpPutConfig = (dados, idPauta) => {
        setErro("");
        setConfig({
            url: `http://localhost:8080/v1/pautas/${idPauta}`,
            config: {
                method: "PUT",
                headers: {
                    "Content-type": "application/json"
                }
            },
            body: JSON.stringify(dados)
        })



    }

    const limparErro = () => setErro("");
    useEffect(() => {
        const httpRequest = async () => {
            if (!config) return;
            setCarregamento(true);
            console.log(url)
            try {
                const requisicao = await fetch(config, config);
                if (!requisicao.ok) {
                    let erroRequisicao = await requisicao.json();
                    throw { mensagem: erroRequisicao.mensagem, status: erroRequisicao.status }
                }

            } catch (error) {
                setErro({
                    mensagem: error.mensagem,
                    status: error.status
                });
                return null
            }
            finally {
                setCarregamento(true);
            }

        };
        httpRequest();
    }, [config, url])
    return { httpPutConfig, carregamento, erro, limparErro }

}
