import { useEffect, useState } from "react";

export const usePost = () => {
    const [config, setConfig] = useState(null);
    const [metodo, setMetodo] = useState(null);
    const [url, setUrl] = useState(null);
    const [carregamento, setCarregamento] = useState(false);
    const [erro, setErro] = useState(null);

    const httpConfig = (dados, metodo) => {

        setErro(null);

        setConfig({
            method: metodo,
            headers: {
                "Content-type": "application/json"
            },
            body: JSON.stringify(dados)
        })
        setMetodo(metodo);
    };
     const limparErro = () => setErro(null);
    useEffect(() => {
        const httpRequest = async () => {
            if (!config) return;
            setCarregamento(true);
            try {
             const requisicao = await fetch(url, config);
                if (!requisicao.create) {
                    const erroTexto = await requisicao.json();
                    throw {mensagem: erroTexto.mensagem, status: erroTexto.status};
                }
            } catch (error) {
                setErro({
                    mensagem: error.mensagem || "Erro desconhecido",
                    status: error.status || 500
                });
                return null;
            } finally {
                setCarregamento(false);
            }

        };
        httpRequest();
    }, [config, metodo, url])
    return { httpConfig, carregamento, erro, limparErro }
};
