import { useEffect, useState } from "react";

export const usePost = (url) => {
    const [config, setConfig] = useState(null);
    const [metodo, setMetodo] = useState(null);
    const [carregamento, setCarregamento] = useState(false);
    const [erro, setErro] = useState(null);

    const httpConfig = (dados, metodo) => {
    
        setErro(null);

        setConfig({
            method: metodo,
            headers: {
                "Content-type": "application/json"
            },
            body: JSON.stringify(dados)
        })
        setMetodo(metodo);
    };
    useEffect(() => {
        const httpRequest = async () => {
              if (!config) return;
            setCarregamento(true);
            try {
                let opcoes = [url, config];
                const requisicao = await fetch(...opcoes);
                  if (!requisicao.create) {
                const erroTexto = await  requisicao.text(); 
                throw new Error(erroTexto || "Erro na requisição.");
            }
            } catch (error) {
                setErro(error.message);
                return null;
            }finally {
                setCarregamento(false);
            }
            
        };
        httpRequest();
    }, [config, metodo, url])
    return { httpConfig, carregamento, erro }
};
