import React, { useState } from "react";
import "./style.css";
import login from "./assets/login.png";
import icon from "./assets/icon.png";

export const LoginCadastro = () => {
    // ðŸ”¸ Estados
    const [modoCadastro, setModoCadastro] = useState(false);
    const [nome, setNome] = useState("");
    const [cpf, setCpf] = useState("");
    const [senha, setSenha] = useState("");
    const [mensagemErro, setMensagemErro] = useState("");

    // ðŸ”¸ Alternar entre login e cadastro
    const alternarModo = () => {
        setModoCadastro(!modoCadastro);
        setMensagemErro(""); // Limpa erro ao trocar de modo
        setNome("");
        setCpf("");
        setSenha("");
    };

    // ðŸ”¸ ValidaÃ§Ã£o de dados
    const realizarEnvio = () => {
        if (!cpf || !senha || (modoCadastro && !nome)) {
            setMensagemErro("Preencha todos os campos.");
            return;
        }

        if (!modoCadastro) {
            // ðŸ”¸ SimulaÃ§Ã£o de login
            const cpfValido = "12345678900";
            const senhaValida = "senha123";

            if (cpf !== cpfValido || senha !== senhaValida) {
                setMensagemErro("CPF ou senha invÃ¡lidos.");
                return;
            }

            setMensagemErro("");
            alert("Login realizado com sucesso!");
        } else {
            // ðŸ”¸ SimulaÃ§Ã£o de cadastro
            setMensagemErro("");
            alert("Cadastro realizado com sucesso!");
        }
    };

    return (
        <div className="login">
            <div className="conteudo">

                {/* ðŸ”¹ LADO ESQUERDO - Imagem */}
                <div className="lado-esquerdo">
                    <img src={login} alt="LoginCadastro" style={{ width: "100vh" }} />
                </div>

                {/* ðŸ”¹ LADO DIREITO - FormulÃ¡rio */}
                <div className="lado-direito">
                    <img src={icon} alt="LoginCadastro" style={{ width: "65px" }} />
                    <div className="formulario">

                        {/* ðŸ”¸ Campo Nome (somente no cadastro) */}
                        {modoCadastro && (
                            <div className="campo">
                                <div className="rotulo">Nome</div>
                                <input
                                    className="entrada"
                                    type="text"
                                    placeholder="Nome completo"
                                    value={nome}
                                    onChange={(e) => setNome(e.target.value)}
                                />
                            </div>
                        )}

                        {/* ðŸ”¸ Campo CPF */}
                        <div className="campo">
                            <div className="rotulo">CPF</div>
                            <input
                                className={`entrada ${mensagemErro && (cpf === "" || cpf !== "12345678900") ? "erro" : ""}`}
                                type="text"
                                placeholder="Digite seu CPF"
                                value={cpf}
                                onChange={(e) => setCpf(e.target.value)}
                            />
                            {mensagemErro && (cpf === "" || cpf !== "12345678900") && (
                                <div className="mensagem-erro-campo">CPF invÃ¡lido.</div>
                            )}
                        </div>

                        {/* ðŸ”¸ Campo Senha */}
                        <div className="campo">
                            <div className="rotulo">Senha</div>
                            <input
                                className={`entrada ${mensagemErro && (senha === "" || senha !== "senha123") ? "erro" : ""}`}
                                type="password"
                                placeholder="Digite sua senha"
                                value={senha}
                                onChange={(e) => setSenha(e.target.value)}
                            />
                            {mensagemErro && (senha === "" || senha !== "senha123") && (
                                <div className="mensagem-erro-campo">Senha incorreta.</div>
                            )}
                        </div>


                        {/* ðŸ”¸ Link "Esqueceu a senha" (somente no login) */}
                        {!modoCadastro && (
                            <div className="link-cadastro">
                                <div className="descricao">Esqueceu a senha ?</div>
                            </div>
                        )}

                        {/* ðŸ”¸ Mensagem de erro */}
                        {mensagemErro && (
                            <div className="mensagem-erro">{mensagemErro}</div>
                        )}

                        {/* ðŸ”¸ BotÃ£o de aÃ§Ã£o */}
                        <div className="grupo-botao">
                            <button className="botao" onClick={realizarEnvio}>
                                <span className="texto-botao">
                                    {modoCadastro ? "Cadastrar" : "Acessar"}
                                </span>
                            </button>
                        </div>
                    </div>

                    {/* ðŸ”¸ Link para alternar modo */}
                    <div className="texto-recuperar" onClick={alternarModo}>
                        {modoCadastro ? "JÃ¡ tenho uma conta" : "Cadastre-se"}
                    </div>
                </div>
            </div>
        </div>
    );
};
