import React, { useState } from "react";
import "./MenuPrincipal.css";
import icon from "./assets/icon.png";
import lateralImage from "./assets/menu.png";

export const MenuPrincipal = ({ numeroUsuario, cpfUsuario }) => {
    const nomeUsuario = "Wanderllan Santos";

    const [votacoes, setVotacoes] = useState([
        { titulo: "Qual seu estilo musical favorito?", autor: "João Silva", totalVotos: 120 },
        { titulo: "Melhor série do momento", autor: "Maria Souza", totalVotos: 85 },
        { titulo: "Você prefere café ou chá?", autor: "Wanderllan Santos", totalVotos: 60 },
        { titulo: "Qual seu estilo musical favorito?", autor: "João Silva", totalVotos: 120 },
        { titulo: "Melhor série do momento", autor: "Maria Souza", totalVotos: 85 },
        { titulo: "Você prefere café ou chá?", autor: "Wanderllan Santos", totalVotos: 60 }
    ]);

    const [votacaoAberta, setVotacaoAberta] = useState(null);
    const [criandoVotacao, setCriandoVotacao] = useState(false);
    const [editandoVotacaoIndex, setEditandoVotacaoIndex] = useState(null);

    const [novaVotacao, setNovaVotacao] = useState({
        titulo: "",
        opcao1: "",
        opcao2: ""
    });

    const [votacoesVotadas, setVotacoesVotadas] = useState([]); // <- controle de votos únicos

    const abrirVotacao = (index) => {
        setVotacaoAberta(index);
    };

    const fecharVotacao = () => {
        setVotacaoAberta(null);
    };

    const iniciarCriacao = () => {
        setNovaVotacao({ titulo: "", opcao1: "", opcao2: "" });
        setCriandoVotacao(true);
        setEditandoVotacaoIndex(null);
    };

    const salvarNovaVotacao = () => {
        if (editandoVotacaoIndex !== null) {
            const atualizadas = [...votacoes];
            atualizadas[editandoVotacaoIndex].titulo = novaVotacao.titulo;
            setVotacoes(atualizadas);
        } else {
            setVotacoes([...votacoes, {
                titulo: novaVotacao.titulo,
                autor: nomeUsuario,
                totalVotos: 0
            }]);
        }
        setCriandoVotacao(false);
    };

    const editarVotacao = (index) => {
        setNovaVotacao({
            titulo: votacoes[index].titulo,
            opcao1: "Opção A",
            opcao2: "Opção B"
        });
        setEditandoVotacaoIndex(index);
        setCriandoVotacao(true);
    };

    const cancelarCriacao = () => {
        setCriandoVotacao(false);
    };

    const votar = () => {
        if (votacoesVotadas.includes(votacaoAberta)) return;

        const novasVotacoes = [...votacoes];
        novasVotacoes[votacaoAberta].totalVotos += 1;
        setVotacoes(novasVotacoes);
        setVotacoesVotadas([...votacoesVotadas, votacaoAberta]);
    };

    return (
        <div className="menu">
            <div className="barra-superior">
                <div className="coluna info-usuario">
                    <span className="nome-usuario">{nomeUsuario} {numeroUsuario}</span>
                    <span className="cpf-usuario">{cpfUsuario}</span>
                </div>
                <div className="coluna imagem-logo">
                    <img src={icon} alt="Logo" />
                </div>
                <div className="coluna botao-wrapper">
                    <button className="botao-deslogar" onClick={() => console.log("Deslogado")}>Sair</button>
                </div>
            </div>

            <div className="imagem-lateral">
                <img src={lateralImage} alt="Descrição visual" />
            </div>

            {criandoVotacao ? (
                <div className="caixa-votacao-aberta">
                    <h3>{editandoVotacaoIndex !== null ? "Editar Votação" : "Nova Votação"}</h3>
                    <input
                        type="text"
                        placeholder="Tema da Votação"
                        value={novaVotacao.titulo}
                        onChange={(e) => setNovaVotacao({ ...novaVotacao, titulo: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Opção 1"
                        value={novaVotacao.opcao1}
                        onChange={(e) => setNovaVotacao({ ...novaVotacao, opcao1: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Opção 2"
                        value={novaVotacao.opcao2}
                        onChange={(e) => setNovaVotacao({ ...novaVotacao, opcao2: e.target.value })}
                    />
                    <button className="botao-votar" onClick={salvarNovaVotacao}>Salvar</button>
                    <button className="botao-fechar" onClick={cancelarCriacao}>Cancelar</button>
                </div>
            ) : votacaoAberta !== null ? (
                <div className="caixa-votacao-aberta">
                    <h3>{votacoes[votacaoAberta].titulo}</h3>
                    <p>Criada por <strong>{votacoes[votacaoAberta].autor}</strong></p>
                    <p>Total de votos: {votacoes[votacaoAberta].totalVotos}</p>
                    <ul className="opcoes-votacao">
                        <li onClick={votar} style={{ cursor: "pointer" }}>
                            {novaVotacao.opcao1 || "Opção A"}
                        </li>
                        <li onClick={votar} style={{ cursor: "pointer" }}>
                            {novaVotacao.opcao2 || "Opção B"}
                        </li>
                    </ul>

                    {votacoesVotadas.includes(votacaoAberta) && (
                        <div className="mensagem-voto-unico">
                            ⚠️ Você já votou nesta votação.
                        </div>
                    )}

                    <button className="botao-fechar" onClick={fecharVotacao}>Voltar</button>
                </div>
            ) : (
                <div className="conteudo-principal">
                    <div className="texto-votacoes">
                        <h3>Votações em Aberto</h3>
                        <p>Selecione o seu interesse e faça sua votação</p>
                    </div>

                    <div className="caixa-votacoes">
                        <ul className="lista-votacoes">
                            {votacoes.map((votacao, index) => (
                                <li key={index} onClick={() => abrirVotacao(index)}>
                                    <strong>{votacao.titulo}</strong>
                                    <div className="subinfo">
                                        Por: {votacao.autor} • {votacao.totalVotos} votos
                                        {votacao.autor === nomeUsuario && (
                                            <span
                                                style={{ float: "right", color: "#007BFF", cursor: "pointer" }}
                                                onClick={(e) => {
                                                    e.stopPropagation();
                                                    editarVotacao(index);
                                                }}
                                            >
                                                Editar
                                            </span>
                                        )}
                                    </div>
                                </li>
                            ))}
                            <li onClick={iniciarCriacao} style={{ backgroundColor: "#fff0f0", fontWeight: "bold" }}>
                                + Nova Votação
                            </li>
                        </ul>
                    </div>
                </div>
            )}

            <footer className="rodape">
                <div className="header">
                    <h1>Turma da Unifacol</h1>
                    <h2>Sistema de Informação 5º Período</h2>
                </div>
            </footer>
        </div>
    );
};
