import React, { useState, useEffect } from "react";
import "./MenuPrincipal.css";
import icon from "./assets/icon.png";
import lateralImage from "./assets/menu.png";

export const MenuPrincipal = ({ numeroUsuario, cpfUsuario }) => {
    const nomeUsuario = "Wanderllan Santos";

    const [votacoes, setVotacoes] = useState([
        {
            titulo: "Qual seu estilo musical favorito?",
            autor: "João Silva",
            votosOpcao1: 70,
            votosOpcao2: 50,
            totalVotos: 120,
            opcao1: "Rock",
            opcao2: "Pop",
            usuariosVotaram: [],
        },
        {
            titulo: "Melhor série do momento",
            autor: "Maria Souza",
            votosOpcao1: 45,
            votosOpcao2: 40,
            totalVotos: 85,
            opcao1: "Breaking Bad",
            opcao2: "Stranger Things",
            usuariosVotaram: [],
        },
        {
            titulo: "Você prefere café ou chá?",
            autor: "Wanderllan Santos",
            votosOpcao1: 35,
            votosOpcao2: 25,
            totalVotos: 60,
            opcao1: "Café",
            opcao2: "Chá",
            usuariosVotaram: [],
        },
    ]);

    const [votacaoAberta, setVotacaoAberta] = useState(null);
    const [criandoVotacao, setCriandoVotacao] = useState(false);
    const [editandoVotacaoIndex, setEditandoVotacaoIndex] = useState(null);
    const [novaVotacao, setNovaVotacao] = useState({
        titulo: "",
        opcao1: "",
        opcao2: "",
    });

    const [mensagem, setMensagem] = useState(""); // mensagens de voto

    // Abre votação
    const abrirVotacao = (index) => {
        setMensagem("");
        setVotacaoAberta(index);
    };

    const fecharVotacao = () => {
        setMensagem("");
        setVotacaoAberta(null);
    };

    // Iniciar criação ou edição
    const iniciarCriacao = () => {
        setNovaVotacao({ titulo: "", opcao1: "", opcao2: "" });
        setCriandoVotacao(true);
        setEditandoVotacaoIndex(null);
        setMensagem("");
    };

    // Salvar nova votação ou edição
    const salvarNovaVotacao = () => {
        if (!novaVotacao.titulo || !novaVotacao.opcao1 || !novaVotacao.opcao2) {
            alert("Preencha todos os campos!");
            return;
        }

        if (editandoVotacaoIndex !== null) {
            const atualizadas = [...votacoes];
            atualizadas[editandoVotacaoIndex] = {
                ...atualizadas[editandoVotacaoIndex],
                titulo: novaVotacao.titulo,
                opcao1: novaVotacao.opcao1,
                opcao2: novaVotacao.opcao2,
            };
            setVotacoes(atualizadas);
        } else {
            setVotacoes([
                ...votacoes,
                {
                    titulo: novaVotacao.titulo,
                    autor: nomeUsuario,
                    votosOpcao1: 0,
                    votosOpcao2: 0,
                    totalVotos: 0,
                    opcao1: novaVotacao.opcao1,
                    opcao2: novaVotacao.opcao2,
                    usuariosVotaram: [],
                },
            ]);
        }
        setCriandoVotacao(false);
        setMensagem("");
    };

    // Editar votação existente
    const editarVotacao = (index) => {
        const v = votacoes[index];
        setNovaVotacao({
            titulo: v.titulo,
            opcao1: v.opcao1,
            opcao2: v.opcao2,
        });
        setEditandoVotacaoIndex(index);
        setCriandoVotacao(true);
        setMensagem("");
    };

    const cancelarCriacao = () => {
        setCriandoVotacao(false);
        setMensagem("");
    };

    // Função para votar
    const votar = (opcao) => {
        const v = votacoes[votacaoAberta];
        const usuarioJaVotou = v.usuariosVotaram.includes(numeroUsuario);

        if (usuarioJaVotou) {
            setMensagem("⚠️ Você já votou nesta votação.");
            return;
        }

        // Atualiza votos
        const atualizadas = [...votacoes];
        if (opcao === 1) atualizadas[votacaoAberta].votosOpcao1 += 1;
        else atualizadas[votacaoAberta].votosOpcao2 += 1;

        atualizadas[votacaoAberta].totalVotos += 1;
        atualizadas[votacaoAberta].usuariosVotaram.push(numeroUsuario);

        setVotacoes(atualizadas);
        setMensagem("Obrigado pela sua opinião!");

        // Depois de 2s retorna ao menu principal
        setTimeout(() => {
            setMensagem("");
            fecharVotacao();
        }, 2000);
    };

    // Calcula porcentagem para a barra
    const getPorcentagem = (votosOpcao, total) => {
        if (total === 0) return 0;
        return Math.round((votosOpcao / total) * 100);
    };

    return (
        <div className="menu">
            <div className="barra-superior">
                <div className="coluna info-usuario">
          <span className="nome-usuario">
            {nomeUsuario} {numeroUsuario}
          </span>
                    <span className="cpf-usuario">{cpfUsuario}</span>
                </div>
                <div className="coluna imagem-logo">
                    <img src={icon} alt="Logo" />
                </div>
                <div className="coluna botao-wrapper">
                    <button className="botao-deslogar" onClick={() => console.log("Deslogado")}>
                        Sair
                    </button>
                </div>
            </div>

            <div className="imagem-lateral">
                <img src={lateralImage} alt="Descrição visual" />
            </div>

            {criandoVotacao ? (
                <div className="caixa-votacao-aberta">
                    <h3>{editandoVotacaoIndex !== null ? "Editar Votação" : "Nova Votação"}</h3>
                    <input
                        type="text"
                        placeholder="Tema da Votação"
                        value={novaVotacao.titulo}
                        onChange={(e) => setNovaVotacao({ ...novaVotacao, titulo: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Opção 1"
                        value={novaVotacao.opcao1}
                        onChange={(e) => setNovaVotacao({ ...novaVotacao, opcao1: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Opção 2"
                        value={novaVotacao.opcao2}
                        onChange={(e) => setNovaVotacao({ ...novaVotacao, opcao2: e.target.value })}
                    />
                    <button className="botao-votar" onClick={salvarNovaVotacao}>
                        Salvar
                    </button>
                    <button className="botao-fechar" onClick={cancelarCriacao}>
                        Cancelar
                    </button>
                </div>
            ) : votacaoAberta !== null ? (
                <div className="caixa-votacao-aberta">
                    <h3>{votacoes[votacaoAberta].titulo}</h3>
                    <p>
                        Criada por <strong>{votacoes[votacaoAberta].autor}</strong>
                    </p>
                    <p>Total de votos: {votacoes[votacaoAberta].totalVotos}</p>

                    <div
                        className="opcao-votacao"
                        style={{ cursor: "pointer", marginBottom: 10 }}
                        onClick={() => votar(1)}
                    >
                        <strong>{votacoes[votacaoAberta].opcao1}</strong>
                        <div className="barra-votos" style={{ background: "#eee", height: 20, borderRadius: 10 }}>
                            <div
                                style={{
                                    width: `${getPorcentagem(votacoes[votacaoAberta].votosOpcao1, votacoes[votacaoAberta].totalVotos)}%`,
                                    background: "#4caf50",
                                    height: "100%",
                                    borderRadius: 10,
                                    transition: "width 0.5s ease",
                                }}
                            />
                        </div>
                        <small>
                            {getPorcentagem(votacoes[votacaoAberta].votosOpcao1, votacoes[votacaoAberta].totalVotos)}% (
                            {votacoes[votacaoAberta].votosOpcao1} votos)
                        </small>
                    </div>

                    <div
                        className="opcao-votacao"
                        style={{ cursor: "pointer", marginBottom: 10 }}
                        onClick={() => votar(2)}
                    >
                        <strong>{votacoes[votacaoAberta].opcao2}</strong>
                        <div className="barra-votos" style={{ background: "#eee", height: 20, borderRadius: 10 }}>
                            <div
                                style={{
                                    width: `${getPorcentagem(votacoes[votacaoAberta].votosOpcao2, votacoes[votacaoAberta].totalVotos)}%`,
                                    background: "#2196f3",
                                    height: "100%",
                                    borderRadius: 10,
                                    transition: "width 0.5s ease",
                                }}
                            />
                        </div>
                        <small>
                            {getPorcentagem(votacoes[votacaoAberta].votosOpcao2, votacoes[votacaoAberta].totalVotos)}% (
                            {votacoes[votacaoAberta].votosOpcao2} votos)
                        </small>
                    </div>

                    {mensagem && (
                        <p
                            style={{
                                marginTop: 10,
                                color: mensagem.includes("⚠️") ? "red" : "green",
                                fontWeight: "bold",
                            }}
                        >
                            {mensagem}
                        </p>
                    )}

                    <button className="botao-fechar" onClick={fecharVotacao}>
                        Voltar
                    </button>
                </div>
            ) : (
                <div className="conteudo-principal">
                    <div className="texto-votacoes">
                        <h3>Votações em Aberto</h3>
                        <p>Selecione o seu interesse e faça sua votação</p>
                    </div>

                    <div className="caixa-votacoes">
                        <ul className="lista-votacoes">
                            {votacoes.map((votacao, index) => (
                                <li key={index} onClick={() => abrirVotacao(index)}>
                                    <strong>{votacao.titulo}</strong>
                                    <div className="subinfo">
                                        Por: {votacao.autor} • {votacao.totalVotos} votos
                                        {votacao.autor === nomeUsuario && (
                                            <span
                                                style={{ float: "right", color: "#007BFF", cursor: "pointer" }}
                                                onClick={(e) => {
                                                    e.stopPropagation();
                                                    editarVotacao(index);
                                                }}
                                            >
                        Editar
                      </span>
                                        )}
                                    </div>
                                </li>
                            ))}
                            <li
                                onClick={iniciarCriacao}
                                style={{ backgroundColor: "#fff0f0", fontWeight: "bold", cursor: "pointer" }}
                            >
                                + Nova Votação
                            </li>
                        </ul>
                    </div>
                </div>
            )}

            <footer className="rodape">
                <div className="header">
                    <h1>Turma da Unifacol</h1>
                    <h2>Sistema de Informação 5º Período</h2>
                </div>
            </footer>
        </div>
    );
};
